const getNodeJson = (nodeName) => {
  try {
    const item = $(nodeName).first();
    return item?.json ?? {};
  } catch (error) {
    console.log('Error getting node', nodeName, ':', error.message);
    return {};
  }
};

// Try to get context from either Demo-Daten or JSON parsen (Rollen)
const demoData = getNodeJson('Demo-Daten (Alle)');
const apiData = getNodeJson('JSON parsen (Rollen)');
const contextData = demoData.unternehmen ? demoData : apiData;

const processData = getNodeJson('APQC Prozesse filtern');
const activityData = getNodeJson('APQC Aktivitäten filtern');

// DEBUG: Zeige was von den anderen Nodes kommt
console.log('=== KONTEXT + PROZESSE DEBUG ===');
console.log('Demo data has unternehmen:', !!demoData.unternehmen);
console.log('Demo data has mitarbeiter:', demoData.mitarbeiter?.length ?? 0);
console.log('API data has unternehmen:', !!apiData.unternehmen);
console.log('Using demo data:', demoData.unternehmen ? true : false);
console.log('Process data keys:', Object.keys(processData));
console.log('APQC Processes:', processData.apqcProcesses?.length ?? 0);
console.log('First 3 APQC:', processData.apqcProcesses?.slice(0, 3).map(p => ({ pcf_id: p.pcf_id, name: p.name })));

return [{
  json: {
    ...contextData,
    apqcProcesses: processData.apqcProcesses ?? [],
    apqcActivities: activityData.apqcActivities ?? [],
    apqcActivitiesByProcess: activityData.apqcActivitiesByProcess ?? {},
  },
}];

const items = $input.all();
let rows = [];
for (const item of items) {
  const data = item.json;
  if (Array.isArray(data)) {
    rows = rows.concat(data);
  } else if (data && Array.isArray(data.data)) {
    rows = rows.concat(data.data);
  } else if (data) {
    rows.push(data);
  }
}
const processes = rows
  .filter(row => String(row['Hierarchy'] ?? row['Hierarchy ID'] ?? '').trim() === '3')
  .map((row, index) => {
    const hierarchyId = String(row['Hierarchy ID'] ?? '').trim();
    const pcfId = String(row['PCF ID'] ?? '').trim();
    const rawName = row['Name (German Translation) (not verified)'] ?? row['Name (German Translation) (not veriied)'] ?? row['Name'];
    const rawDescription = row['Element Description (German Translation) (not verified)'] ?? row['Element Description (German Translation) (not verfied)'] ?? row['Element Description'];
    const name = typeof rawName === 'string' ? rawName.trim() : '';
    const description = typeof rawDescription === 'string' ? rawDescription.trim() : '';
    return {
      source_index: index,
      pcf_id: pcfId,
      hierarchy_id: hierarchyId,
      name,
      description
    };
  })
  .filter(process => process.pcf_id && process.hierarchy_id && process.name && process.hierarchy_id.split('.').length === 3);
return [{ json: { apqcProcesses: processes } }];

const getNodeJson = (nodeName) => {
  try {
    const item = $(nodeName).first();
    return item?.json ?? {};
  } catch (error) {
    return {};
  }
};

const prozessData = getNodeJson('JSON parsen (Prozesse)');
const {
  unternehmen = {},
  abteilungen = [],
  rollen = [],
  prozesse = [],
  mitarbeiter: existingMitarbeiter = [],
  apqcProcesses = [],
  apqcActivities = [],
  apqcActivitiesByProcess = {},
  config = {},
} = prozessData;

// Wenn bereits Mitarbeiter vorhanden sind (Demo-Daten), diese verwenden
if (existingMitarbeiter && existingMitarbeiter.length > 0) {
  console.log('Using existing demo employees:', existingMitarbeiter.length);
  return { unternehmen, abteilungen, rollen, prozesse, mitarbeiter: existingMitarbeiter, apqcProcesses, apqcActivities, apqcActivitiesByProcess, config };
}

// Sonst API Response parsen
const message = $input.first().json?.choices?.[0]?.message;
if (!message) {
  throw new Error('Antwort der Mitarbeiter-API fehlt oder ist leer.');
}

let raw = message.content;
if (Array.isArray(raw)) {
  const jsonPart = raw.find(part => part.type === 'json_schema' || part.type === 'json_object' || part.type === 'output_json');
  if (jsonPart?.json) {
    raw = jsonPart.json;
  } else {
    raw = raw.map(part => part.text ?? '').join('\n');
  }
}

if (typeof raw === 'object') {
  raw = JSON.stringify(raw);
}

const data = JSON.parse(raw);
let mitarbeiter = data.mitarbeiter ?? data;

mitarbeiter = mitarbeiter.map((ma, index) => ({
  ...ma,
  id: `emp_${String(index + 1).padStart(3, '0')}`,
  unternehmen_id: unternehmen.id ?? '',
  vollname: `${ma.vorname ?? ''} ${ma.nachname ?? ''}`.trim(),
}));

return { unternehmen, abteilungen, rollen, prozesse, mitarbeiter, apqcProcesses, apqcActivities, apqcActivitiesByProcess, config };

