        rolle_titel: rolle.titel,
        rolle_ebene: rolle.ebene,
        abteilung_id: mitarbeiter.abteilung_id,
        abteilung_name: abteilung.name,
        erfahrung_jahre: mitarbeiter.alter ? Math.floor((mitarbeiter.alter - 20) / 2) : 5
      },
      prozess_kontext: {
        pcf_id: prozess.pcf_id,
        name: prozess.name,
        abteilung_name: prozess.abteilung_name
      },
      config: config
    }
  };
});

return aktivitaetenMitKontext;

