// Hole die ursprüngliche Aktivität und die API-Antwort
const originalAktivitaet = $input.first().json;
const apiResponse = $input.first().json.choices?.[0]?.message?.content;

if (!apiResponse) {
  // Falls keine Antwort, gebe Originalaktivität zurück
  const { mitarbeiter_kontext, prozess_kontext, config, ...aktivitaetOhneTemp } = originalAktivitaet;
  return [{
    json: {
      ...aktivitaetOhneTemp,
      beschreibung_mitarbeiter: 'Beschreibung konnte nicht generiert werden.'
    }
  }];
}

// Parse die API-Antwort
let parsedResponse;
try {
  parsedResponse = typeof apiResponse === 'string' ? JSON.parse(apiResponse) : apiResponse;
} catch (e) {
  parsedResponse = { beschreibung_mitarbeiter: 'Fehler beim Parsen der Beschreibung.' };
}

// Entferne die temporären Kontextfelder und füge die Mitarbeiterbeschreibung hinzu
const { mitarbeiter_kontext, prozess_kontext, config, choices, ...aktivitaetOhneTemp } = originalAktivitaet;

return [{
  json: {
    ...aktivitaetOhneTemp,
    beschreibung_mitarbeiter: parsedResponse.beschreibung_mitarbeiter || 'Keine Beschreibung verfügbar.'
  }
}];

// Sammle alle angereicherten Aktivitäten
const aktivitaeten = $input.all().map(item => item.json);

return [{ json: { aktivitaeten } }];

const input = $input.first()?.json ?? {};
const {
  unternehmen = {},
  abteilungen = [],
  rollen = [],
  apqcProcesses = [],
  apqcActivities = [],
  apqcActivitiesByProcess = {},
  config = {},
} = input;

const sortAbteilungen = (list) => {
  return [...list]
    .map((abteilung, index) => ({ ...abteilung, __index: index }))
    .sort((a, b) => {
      const levelA = Number(a.ebene ?? 999);
      const levelB = Number(b.ebene ?? 999);
      if (Number.isFinite(levelA) && Number.isFinite(levelB)) {
        if (levelA !== levelB) return levelA - levelB;
      } else if (Number.isFinite(levelA)) {
        return -1;
      } else if (Number.isFinite(levelB)) {
        return 1;
      }
      const mitA = Number(a.mitarbeiteranzahl ?? 0);
      const mitB = Number(b.mitarbeiteranzahl ?? 0);
      if (mitA !== mitB) return mitB - mitA;
      return (a.name || '').localeCompare(b.name || '');
    });
};

const sortedWithIndex = sortAbteilungen(abteilungen);
const sortedAbteilungen = sortedWithIndex.map(({ __index, ...rest }) => rest);

const globalData = $getWorkflowStaticData('global');
globalData.availableProcesses = Array.isArray(apqcProcesses) ? apqcProcesses.map(proc => ({ ...proc })) : [];
globalData.assignedProcesses = [];
globalData.context = { unternehmen, abteilungen: sortedAbteilungen, rollen, apqcProcesses, apqcActivities, apqcActivitiesByProcess, config };
globalData.remainingDepartments = sortedAbteilungen.length;

// DEBUG: Zeige was initialisiert wurde
console.log('=== PROZESSITERATION VORBEREITEN ===');
console.log('Anzahl Abteilungen:', sortedAbteilungen.length);
console.log('Anzahl APQC Prozesse:', apqcProcesses.length);
console.log('Erste 3 APQC Prozesse:', apqcProcesses.slice(0, 3).map(p => ({ pcf_id: p.pcf_id, name: p.name })));
console.log('Global State initialisiert mit', globalData.availableProcesses.length, 'Prozessen');

return sortedAbteilungen.map((abteilung, index) => ({ json: { abteilung, position: index } }));

const input = $input.first()?.json ?? {};
const abteilung = input.abteilung || {};
const position = input.position ?? 0;

const globalData = $getWorkflowStaticData('global');
const context = globalData.context || {};
const availableProcesses = Array.isArray(globalData.availableProcesses) ? globalData.availableProcesses : [];

const limitProcesses = (processes, limit = 120) => {
  if (processes.length <= limit) return processes;
  return processes.slice(0, limit);
};

const prozessPool = limitProcesses(availableProcesses);
const rollenGesamt = Array.isArray(context.rollen) ? context.rollen : [];
const rollen = rollenGesamt.filter(rolle => rolle.abteilung_id === abteilung.id).slice(0, 10);

const unternehmen = context.unternehmen || {};
const config = context.config || {};

// DEBUG: Prüfe ob Prozesse verfügbar sind
console.log('=== ABTEILUNGSKONTEXT DEBUG ===');
console.log('Abteilung:', abteilung.name);
console.log('Available processes:', availableProcesses.length);
console.log('Process pool:', prozessPool.length);
if (prozessPool.length > 0) {
  console.log('First 3 processes:', prozessPool.slice(0, 3).map(p => ({ pcf_id: p.pcf_id, name: p.name })));
}

// Wenn keine Prozesse verfügbar, erstelle trotzdem einen Request (wird leere Liste zurückgeben)
if (prozessPool.length === 0) {
  console.log('WARNUNG: Keine Prozesse verfügbar! Überspringe diese Abteilung.');
  // Erstelle einen Minimal-Request der eine leere Liste anfordert
  const emptyRequestPayload = {
    model: 'gpt-4o-mini',
    temperature: 0.1,
    response_format: {
      type: 'json_schema',
      json_schema: {
        name: 'prozesse_response',
        schema: {
          type: 'object',
          properties: {
            prozesse: {
              type: 'array',
              items: { type: 'object' }
            }
          },
          required: ['prozesse']
        }
      }
    },
    messages: [
      { role: 'system', content: 'Return empty process list' },
      { role: 'user', content: 'No processes available, return {"prozesse": []}' }
    ]
  };
  return [{ json: {
    unternehmen,
    abteilung,
    rollen,
    prozessPool: [],
    config,
    position,
    requestPayload: emptyRequestPayload,
    skipReason: 'no_processes_available'
  }}];
}

// Erstelle konkrete Beispiele aus den tatsächlich verfügbaren Prozessen
const beispielProzesse = prozessPool.slice(0, 3).map(p =>
  'pcf_id: "' + p.pcf_id + '", hierarchy_id: "' + p.hierarchy_id + '"'
).join(' | ');

const systemPrompt = 'Du bist Organisations- und Prozessberater. Wähle für die Abteilung 1-2 passende APQC Level-3-Prozesse aus der bereitgestellten Liste.\n\n' +
  'KRITISCH - Du MUSST die Prozess-IDs EXAKT aus der verfügbaren Liste kopieren:\n' +
  '1. Suche in der "Verfügbare APQC-Prozesse Level 3" Liste nach passenden Prozessen\n' +
  '2. Kopiere die pcf_id EXAKT aus der Liste (z.B. ' + beispielProzesse + ') - KEINE eigenen IDs erfinden!\n' +
  '3. Kopiere die hierarchy_id EXAKT aus der Liste - KEINE eigenen IDs erfinden!\n' +
  '4. Kopiere name und description EXAKT - Wort für Wort wie in der Liste!\n' +
  '5. Wähle 1-2 Prozesse (bis zu 4 wenn Abteilung sehr komplex)\n' +
  '6. NIEMALS eigene Prozesse erfinden - NUR aus der verfügbaren Liste wählen!\n\n' +
  'Die Liste enthält ' + prozessPool.length + ' verfügbare Prozesse. Wähle NUR Prozesse aus dieser Liste!';

const abteilungsInfo = {
  id: abteilung.id,
  name: abteilung.name,
  ebene: abteilung.ebene,
  mitarbeiter: abteilung.mitarbeiteranzahl,
};

const rollenInfo = rollen.map(rolle => ({
  id: rolle.id,
  titel: rolle.titel,
  ebene: rolle.ebene,
}));

const unternehmensInfo = {
  name: unternehmen.name,
  branche: unternehmen.branche,
  mitarbeiteranzahl: unternehmen.mitarbeiteranzahl,
  besonderheit: config.besonderheit || null,
};

const userSections = [
  'Unternehmenskontext:',
  JSON.stringify(unternehmensInfo, null, 2),
  '',
  'Abteilung:',
  JSON.stringify(abteilungsInfo, null, 2),
  '',
  'Rollen in dieser Abteilung (Auszug):',
  JSON.stringify(rollenInfo, null, 2),
  '',
  'Verfügbare APQC-Prozesse Level 3:',
  JSON.stringify(prozessPool, null, 2),
  '',
  'Aufgabe:',
  'Wähle 1-2 Prozesse aus (mehr nur wenn die Abteilung sehr komplex ist). Die Felder pcf_id, hierarchy_id, name und description müssen EXAKT aus der Liste übernommen werden. Gib zusätzlich eine kurze Begründung (warum passt dieser Prozess) und Priorität (hoch/mittel/niedrig) an. Setze abteilung_id auf "' + abteilung.id + '" und abteilung_name auf "' + abteilung.name + '".',
];

const requestPayload = {
  model: 'gpt-4o-mini',
  temperature: 0.1,
  response_format: {
    type: 'json_schema',
    json_schema: {
      name: 'prozesse_response',
      schema: {
        type: 'object',
        additionalProperties: false,
        properties: {
          prozesse: {
            type: 'array',
            minItems: 1,
            maxItems: 4,
            items: {
              type: 'object',
              additionalProperties: false,
              properties: {
                pcf_id: { type: 'string' },
                hierarchy_id: { type: 'string' },
                name: { type: 'string' },
                description: { type: 'string' },
                abteilung_id: { type: 'string' },
                abteilung_name: { type: 'string' },
                begruendung: { type: 'string' },
                prioritaet: { type: 'string', enum: ['hoch', 'mittel', 'niedrig'] },
              },
              required: ['pcf_id', 'hierarchy_id', 'name', 'description', 'abteilung_id', 'abteilung_name'],
            },
          },
        },
        required: ['prozesse'],
      },
    },
  },
  messages: [
    { role: 'system', content: systemPrompt },
    { role: 'user', content: userSections.join('\n') },
  ],
};

console.log('Request payload created, message length:', JSON.stringify(requestPayload).length);

return [
  {
    json: {
      unternehmen,
      abteilung,
      rollen,
      prozessPool,
      config,
      position,
      requestPayload,
    },
  },
];

const input = $input.first()?.json ?? {};
const response = input.body ?? input;
const message = response.choices?.[0]?.message;
let raw = message?.content;
if (!raw) {
  throw new Error('Antwort der Prozesse-API fehlt oder ist leer.');
}

if (Array.isArray(raw)) {
  const jsonPart = raw.find(part => part.type === 'json_schema' || part.type === 'json_object' || part.type === 'output_json');
  if (jsonPart?.json) {
    raw = jsonPart.json;
  } else {
    raw = raw.map(part => part.text ?? '').join('\n');
  }
}

let data;
if (typeof raw === 'object') {
  data = raw;
} else {
  try {
    data = JSON.parse(raw);
  } catch (error) {
    throw new Error('Antwort konnte nicht als JSON geparst werden: ' + error.message);
  }
}

if (!data || !Array.isArray(data.prozesse)) {
  throw new Error('Die KI-Antwort enthält keine gültige Prozessliste.');
}

// Validierung: Prüfe ob die Prozesse aus dem verfügbaren Pool stammen (nach PCF ID)
const globalData = $getWorkflowStaticData('global');
const available = Array.isArray(globalData.availableProcesses) ? globalData.availableProcesses : [];
const availableByPcfId = new Map(available.map(p => [p.pcf_id, p]));

const validatedProzesse = [];
const warnings = [];

for (const prozess of data.prozesse) {
  if (!prozess.pcf_id) {
    warnings.push(`Prozess ohne pcf_id übersprungen: ${prozess.name || 'unbekannt'}`);
    continue;
  }

  const originalProzess = availableByPcfId.get(prozess.pcf_id);

  if (!originalProzess) {
    warnings.push(`Prozess mit PCF ID "${prozess.pcf_id}" nicht im verfügbaren Pool - AI hat vermutlich eigene ID erfunden!`);
    continue;
  }

  // Verwende IMMER die Original-Daten aus dem Pool
  validatedProzesse.push({
    pcf_id: originalProzess.pcf_id,
    hierarchy_id: originalProzess.hierarchy_id,
    name: originalProzess.name,
    description: originalProzess.description,
    abteilung_id: prozess.abteilung_id,
    abteilung_name: prozess.abteilung_name,
    begruendung: prozess.begruendung || 'Passend für Abteilungsaufgaben',
    prioritaet: prozess.prioritaet || 'mittel',
  });
}

if (warnings.length > 0) {
  console.log('Warnungen bei Prozess-Validierung:', warnings);
}

const assigned = Array.isArray(globalData.assignedProcesses) ? globalData.assignedProcesses : [];
assigned.push(...validatedProzesse);
globalData.assignedProcesses = assigned;

// Debug-Info
const debug = {
  aiRawProzesse: data.prozesse.length,
  validatedCount: validatedProzesse.length,
  totalAssigned: assigned.length,
  availablePoolSize: available.length,
};

return [
  {
    json: {
      prozesse: validatedProzesse,
      warnings: warnings.length > 0 ? warnings : undefined,
      debug,
    },
  },
];

const input = $input.first()?.json ?? {};
const selected = Array.isArray(input.prozesse) ? input.prozesse : [];
const globalData = $getWorkflowStaticData('global');
const available = Array.isArray(globalData.availableProcesses) ? globalData.availableProcesses : [];

// Verwende PCF ID als eindeutigen Identifier (eindeutig und wird nicht neu vergeben)
const selectedPcfIds = new Set(selected.map(p => p.pcf_id).filter(id => id));
const remaining = available.filter(proc => !selectedPcfIds.has(proc.pcf_id));

globalData.availableProcesses = remaining;
const currentRemaining = Number(globalData.remainingDepartments ?? 0);
const updatedRemaining = Math.max(0, currentRemaining - 1);
globalData.remainingDepartments = updatedRemaining;

const isFinal = updatedRemaining === 0;

// Debug-Info
const debug = {
  selectedCount: selected.length,
  selectedPcfIds: Array.from(selectedPcfIds),
  remainingCount: remaining.length,
  departmentsLeft: updatedRemaining,
  isFinal,
};

return [
  {
    json: {
      prozesse: selected,
      isFinal,
      debug,
    },
  },
];

const input = $input.first()?.json ?? {};
if (!input.isFinal) {
  return [];
}

const globalData = $getWorkflowStaticData('global');
const context = globalData.context || {};
const assignedProcesses = globalData.assignedProcesses || [];

// Debug: Zeige wie viele Prozesse zugewiesen wurden
console.log('FINALE PROZESSLISTE:', {
  totalProcesses: assignedProcesses.length,
  processes: assignedProcesses,
});

const result = {
  unternehmen: context.unternehmen || {},
  abteilungen: context.abteilungen || [],
  rollen: context.rollen || [],
  prozesse: assignedProcesses,
  apqcProcesses: context.apqcProcesses || [],
  apqcActivities: context.apqcActivities || [],
  apqcActivitiesByProcess: context.apqcActivitiesByProcess || {},
  config: context.config || {},
};

// ERST NACH dem Erstellen des Results die globalen Daten löschen
globalData.availableProcesses = [];
globalData.assignedProcesses = [];
globalData.context = {};
globalData.remainingDepartments = 0;

return [
  {
    json: result,
  },
];
