  if (jsonPart && jsonPart.json) {
    raw = jsonPart.json;
  } else {
    raw = raw.map(part => part.text ?? '').join('\n');
  }
}
if (typeof raw === 'object') {
  raw = JSON.stringify(raw);
}
const data = JSON.parse(raw);
const aktivitaeten = data.aktivitaeten ?? data;
return { unternehmen, abteilungen, rollen, prozesse, mitarbeiter, aktivitaeten, apqcProcesses, apqcActivities, apqcActivitiesByProcess, config };

// Hole alle Daten aus dem vorherigen Node
const { unternehmen, abteilungen, rollen, prozesse, mitarbeiter, aktivitaeten, apqcProcesses, apqcActivities, apqcActivitiesByProcess, config } = $input.first().json;

// Erstelle Lookup-Maps fÃ¼r schnellen Zugriff
const mitarbeiterMap = new Map(mitarbeiter.map(m => [m.id, m]));
const rollenMap = new Map(rollen.map(r => [r.id, r]));
const abteilungenMap = new Map(abteilungen.map(a => [a.id, a]));
const prozesseMap = new Map(prozesse.map(p => [p.pcf_id, p]));

// Reichere jede AktivitÃ¤t mit vollstÃ¤ndigem Kontext an
const aktivitaetenMitKontext = aktivitaeten.map(aktivitaet => {
  const mitarbeiter = mitarbeiterMap.get(aktivitaet.mitarbeiter_id) || {};
  const rolle = rollenMap.get(mitarbeiter.rolle_id) || {};
  const abteilung = abteilungenMap.get(mitarbeiter.abteilung_id) || {};
  const prozess = prozesseMap.get(aktivitaet.prozess_id) || {};

  return {
    json: {
      ...aktivitaet,
      mitarbeiter_kontext: {
        id: mitarbeiter.id,
        vollname: mitarbeiter.vollname,
        vorname: mitarbeiter.vorname,
