// Hole die ursprÃ¼ngliche AktivitÃ¤t und die API-Antwort
const originalAktivitaet = $input.first().json;
const apiResponse = $input.first().json.choices?.[0]?.message?.content;

if (!apiResponse) {
  // Falls keine Antwort, gebe OriginalaktivitÃ¤t zurÃ¼ck
  const { mitarbeiter_kontext, prozess_kontext, config, ...aktivitaetOhneTemp } = originalAktivitaet;
  return [{
    json: {
      ...aktivitaetOhneTemp,
      beschreibung_mitarbeiter: 'Beschreibung konnte nicht generiert werden.'
    }
  }];
}

// Parse die API-Antwort
let parsedResponse;
try {
  parsedResponse = typeof apiResponse === 'string' ? JSON.parse(apiResponse) : apiResponse;
} catch (e) {
  parsedResponse = { beschreibung_mitarbeiter: 'Fehler beim Parsen der Beschreibung.' };
}

// Entferne die temporÃ¤ren Kontextfelder und fÃ¼ge die Mitarbeiterbeschreibung hinzu
const { mitarbeiter_kontext, prozess_kontext, config, choices, ...aktivitaetOhneTemp } = originalAktivitaet;

return [{
  json: {
    ...aktivitaetOhneTemp,
    beschreibung_mitarbeiter: parsedResponse.beschreibung_mitarbeiter || 'Keine Beschreibung verfÃ¼gbar.'
  }
}];

// Sammle alle angereicherten AktivitÃ¤ten
const aktivitaeten = $input.all().map(item => item.json);

return [{ json: { aktivitaeten } }];

const input = $input.first()?.json ?? {};
const {
  unternehmen = {},
  abteilungen = [],
  rollen = [],
  apqcProcesses = [],
  apqcActivities = [],
  apqcActivitiesByProcess = {},
  config = {},
} = input;

const sortAbteilungen = (list) => {
  return [...list]
    .map((abteilung, index) => ({ ...abteilung, __index: index }))
    .sort((a, b) => {
      const levelA = Number(a.ebene ?? 999);
      const levelB = Number(b.ebene ?? 999);
      if (Number.isFinite(levelA) && Number.isFinite(levelB)) {
        if (levelA !== levelB) return levelA - levelB;
      } else if (Number.isFinite(levelA)) {
        return -1;
      } else if (Number.isFinite(levelB)) {
        return 1;
      }
      const mitA = Number(a.mitarbeiteranzahl ?? 0);
      const mitB = Number(b.mitarbeiteranzahl ?? 0);
      if (mitA !== mitB) return mitB - mitA;
      return (a.name || '').localeCompare(b.name || '');
    });
};

const sortedWithIndex = sortAbteilungen(abteilungen);
const sortedAbteilungen = sortedWithIndex.map(({ __index, ...rest }) => rest);

const globalData = $getWorkflowStaticData('global');
