const items = $input.all();
let rows = [];
for (const item of items) {
  const data = item.json;
  if (Array.isArray(data)) {
    rows = rows.concat(data);
  } else if (data && Array.isArray(data.data)) {
    rows = rows.concat(data.data);
  } else if (data) {
    rows.push(data);
  }
}
const activities = rows
  .filter(row => String(row['Hierarchy'] ?? row['Hierarchy ID'] ?? '').trim() === '4')
  .map(row => {
    const hierarchyId = String(row['Hierarchy ID'] ?? '').trim();
    const parentHierarchyId = hierarchyId.includes('.') ? hierarchyId.split('.').slice(0, -1).join('.') : '';
    return {
      pcf_id: row['PCF ID'],
      hierarchy_id: hierarchyId,
      parent_hierarchy_id: parentHierarchyId,
      name: row['Name (German Translation) (not veriied)'] ?? row['Name (German Translation) (not verified)'] ?? row['Name'] ?? '',
      description: row['Element Description (German Translation) (not verfied)'] ?? row['Element Description (German Translation) (not verified)'] ?? row['Element Description'] ?? ''
    };
  });
const activitiesByProcess = activities.reduce((acc, activity) => {
  const key = activity.parent_hierarchy_id || '';
  if (!acc[key]) acc[key] = [];
  acc[key].push(activity);
  return acc;
}, {});
return [{ json: { apqcActivities: activities, apqcActivitiesByProcess: activitiesByProcess } }];

// Hole die ursprÃ¼ngliche AktivitÃ¤t und die API-Antwort
const originalAktivitaet = $input.first().json;
const apiResponse = $input.first().json.choices?.[0]?.message?.content;

if (!apiResponse) {
  // Falls keine Antwort, gebe OriginalaktivitÃ¤t zurÃ¼ck
  const { mitarbeiter_kontext, prozess_kontext, config, ...aktivitaetOhneTemp } = originalAktivitaet;
  return [{
    json: {
      ...aktivitaetOhneTemp,
      beschreibung_mitarbeiter: 'Beschreibung konnte nicht generiert werden.',
      beschreibung_qualitaet_motivation: mitarbeiter_kontext.motivation,
      beschreibung_qualitaet_intelligenz: mitarbeiter_kontext.intelligenz,
      beschreibung_qualitaet_gesamt: 'unbekannt'
    }
  }];
}

// Parse die API-Antwort
let parsedResponse;
try {
  parsedResponse = typeof apiResponse === 'string' ? JSON.parse(apiResponse) : apiResponse;
} catch (e) {
  parsedResponse = { beschreibung_mitarbeiter: 'Fehler beim Parsen der Beschreibung.' };
}

// Entferne die temporÃ¤ren Kontextfelder und fÃ¼ge die Mitarbeiterbeschreibung hinzu
const { mitarbeiter_kontext, prozess_kontext, config, choices, ...aktivitaetOhneTemp } = originalAktivitaet;

// Berechne GesamtqualitÃ¤t basierend auf Motivation und Intelligenz
const calculateQuality = (motivation, intelligenz) => {
  const qualityMap = { 'hoch': 3, 'mittel': 2, 'niedrig': 1 };
  const motivScore = qualityMap[motivation] || 2;
  const intScore = qualityMap[intelligenz] || 2;
  const avgScore = (motivScore + intScore) / 2;

  if (avgScore >= 2.5) return 'hoch';
  if (avgScore >= 1.5) return 'mittel';
  return 'niedrig';
};

return [{
  json: {
    ...aktivitaetOhneTemp,
    beschreibung_mitarbeiter: parsedResponse.beschreibung_mitarbeiter || 'Keine Beschreibung verfÃ¼gbar.',
    // QualitÃ¤tsfelder vom Mitarbeiter speichern
    beschreibung_qualitaet_motivation: mitarbeiter_kontext.motivation,
    beschreibung_qualitaet_intelligenz: mitarbeiter_kontext.intelligenz,
    beschreibung_qualitaet_gesamt: calculateQuality(mitarbeiter_kontext.motivation, mitarbeiter_kontext.intelligenz)
  }
}];

// Sammle alle angereicherten AktivitÃ¤ten
const aktivitaeten = $input.all().map(item => item.json);

return [{ json: { aktivitaeten } }];

const input = $input.first()?.json ?? {};

const {
  unternehmen = {},
  abteilungen = [],
  rollen = [],
  apqcProcesses = [],
  apqcActivities = [],
  apqcActivitiesByProcess = {},
  config = {},
} = input;

const globalData = $getWorkflowStaticData('global');

console.log('=== PROZESSITERATION VORBEREITEN ===');
console.log('Config use_demo_prozesse:', config.use_demo_prozesse);

// Wenn Demo-Prozesse verwendet werden sollen, springe direkt zum Abschluss
if (config.use_demo_prozesse) {
  console.log('=== PROZESSITERATION ÃBERSPRUNGEN (use_demo_prozesse aktiv) ===');

  // Hole Demo-Prozesse aus Demo-Daten Node
  const getNodeJson = (nodeName) => {
    try {
      const item = $(nodeName).first();
      return item?.json ?? {};
    } catch (error) {
      return {};
    }
  };

  const demoData = getNodeJson('Demo-Daten (Alle)');
  const demoProzesse = demoData.prozesse || [];

  console.log('Demo-Prozesse gefunden:', demoProzesse.length);

  // Speichere Kontext in globalData fÃ¼r spÃ¤teren Zugriff
  globalData.context = { unternehmen, abteilungen, rollen, apqcProcesses, apqcActivities, apqcActivitiesByProcess, config };
  globalData.assignedProcesses = demoProzesse;

  // Markiere als final fÃ¼r direkten Sprung zu "Prozessiteration Abschluss"
  return [{ json: { isFinal: true } }];
}

console.log('=== STARTE PROZESS-ITERATION ===');
console.log('Anzahl Abteilungen:', abteilungen.length);

const sortAbteilungen = (list) => {
  return [...list]
    .map((abteilung, index) => ({ ...abteilung, __index: index }))
    .sort((a, b) => {
      const levelA = Number(a.ebene ?? 999);
      const levelB = Number(b.ebene ?? 999);
      if (Number.isFinite(levelA) && Number.isFinite(levelB)) {
        if (levelA !== levelB) return levelA - levelB;
      } else if (Number.isFinite(levelA)) {
        return -1;
      } else if (Number.isFinite(levelB)) {
        return 1;
      }
      const mitA = Number(a.mitarbeiteranzahl ?? 0);
      const mitB = Number(b.mitarbeiteranzahl ?? 0);
      if (mitA !== mitB) return mitB - mitA;
      return (a.name || '').localeCompare(b.name || '');
    });
};

const sortedWithIndex = sortAbteilungen(abteilungen);
const sortedAbteilungen = sortedWithIndex.map(({ __index, ...rest }) => rest);

globalData.availableProcesses = Array.isArray(apqcProcesses) ? apqcProcesses.map(proc => ({ ...proc })) : [];
globalData.assignedProcesses = [];
globalData.context = { unternehmen, abteilungen: sortedAbteilungen, rollen, apqcProcesses, apqcActivities, apqcActivitiesByProcess, config };
globalData.remainingDepartments = sortedAbteilungen.length;

return sortedAbteilungen.map((abteilung, index) => ({ json: { abteilung, position: index } }));

const input = $input.first()?.json ?? {};
const abteilung = input.abteilung || {};
const position = input.position ?? 0;

const globalData = $getWorkflowStaticData('global');
const context = globalData.context || {};
const availableProcesses = Array.isArray(globalData.availableProcesses) ? globalData.availableProcesses : [];

const limitProcesses = (processes, limit = 120) => {
  if (processes.length <= limit) return processes;
  return processes.slice(0, limit);
};

const prozessPool = limitProcesses(availableProcesses);
const rollenGesamt = Array.isArray(context.rollen) ? context.rollen : [];
const rollen = rollenGesamt.filter(rolle => rolle.abteilung_id === abteilung.id).slice(0, 10);

const unternehmen = context.unternehmen || {};
const config = context.config || {};

const systemPrompt = "Du bist Organisations- und Prozessberater. Erstelle fÃ¼r die angegebene Abteilung 2-4 passende APQC-Prozesse auf Ebene 3. Verwende ausschlieÃlich die bereitgestellten Prozessdaten (PCF ID, Hierarchy ID, Name, Beschreibung) unverÃ¤ndert. Jeder Prozess darf nur einmal verwendet werden. Erstelle zusÃ¤tzlich fÃ¼r jeden Prozess einen branchenspezifischen Prozessnamen, der auf dem APQC-Namen basiert, aber konkret auf die Branche und TÃ¤tigkeit angepasst ist - OHNE den Unternehmensnamen zu verwenden.";

const abteilungsInfo = {
  id: abteilung.id,
  name: abteilung.name,
  ebene: abteilung.ebene,
  mitarbeiter: abteilung.mitarbeiteranzahl,
};

const rollenInfo = rollen.map(rolle => ({
  id: rolle.id,
  titel: rolle.titel,
  ebene: rolle.ebene,
}));

const unternehmensInfo = {
  name: unternehmen.name,
  branche: unternehmen.branche,
  mitarbeiteranzahl: unternehmen.mitarbeiteranzahl,
  besonderheit: config.besonderheit || null,
};

const userSections = [
  "Unternehmenskontext:",
  JSON.stringify(unternehmensInfo, null, 2),
  "",
  "Abteilung:",
  JSON.stringify(abteilungsInfo, null, 2),
  "",
  "Rollen in dieser Abteilung (Auszug):",
  JSON.stringify(rollenInfo, null, 2),
  "",
  "VerfÃ¼gbare APQC-Prozesse Level 3:",
  JSON.stringify(prozessPool, null, 2),
  "",
  "Aufgabe:",
  "WÃ¤hle 2-4 Prozesse aus der Liste aus, die bestmÃ¶glich zu den Aufgaben der Abteilung passen. Verwende die gelieferten IDs, Namen und Beschreibungen unverÃ¤ndert. Erstelle zusÃ¤tzlich fÃ¼r jeden Prozess einen spezifischer_name, der den generischen APQC-Namen konkret auf die Branche anpasst. Beispiel: Bei einem Eiscreme-Hersteller wird 'Manage Production Operations' zu 'Eiscremeherstellung' oder 'Manage Quality' zu 'QualitÃ¤tskontrolle Speiseeis'. WICHTIG: Verwende NICHT den Unternehmensnamen, sondern nur branchenspezifische Begriffe. Gib je Prozess eine kurze BegrÃ¼ndung und eine PrioritÃ¤t (hoch/mittel/niedrig) an. Antworte im JSON-Format laut Schema.",
];

const requestPayload = {
  model: "gpt-4o-mini",
  temperature: 0.2,
  response_format: {
    type: "json_schema",
    json_schema: {
      name: "prozesse_response",
      schema: {
        type: "object",
        additionalProperties: false,
        properties: {
          prozesse: {
            type: "array",
            minItems: 1,
            items: {
              type: "object",
              additionalProperties: false,
              properties: {
                pcf_id: { type: "string", pattern: "^[0-9]+$" },
                hierarchy_id: { type: "string", pattern: "^[0-9]+\\.[0-9]+\\.[0-9]+$" },
                name: { type: "string" },
                description: { type: "string" },
                spezifischer_name: { type: "string" },
                abteilung_id: { type: "string" },
                abteilung_name: { type: "string" },
                begruendung: { type: "string" },
                prioritaet: { type: "string", enum: ["hoch", "mittel", "niedrig"] },
              },
              required: ["pcf_id", "hierarchy_id", "name", "description", "spezifischer_name", "abteilung_id", "abteilung_name"],
            },
          },
        },
        required: ["prozesse"],
      },
    },
  },
  messages: [
    { role: "system", content: systemPrompt },
    { role: "user", content: userSections.join("\n") },
  ],
};

return [
  {
    json: {
      unternehmen,
      abteilung,
      rollen,
      prozessPool,
      config,
      position,
      requestPayload,
    },
  },
];

const input = $input.first()?.json ?? {};
const response = input.body ?? input;
const message = response.choices?.[0]?.message;
let raw = message?.content;
if (!raw) {
  throw new Error('Antwort der Prozesse-API fehlt oder ist leer.');
}

if (Array.isArray(raw)) {
  const jsonPart = raw.find(part => part.type === 'json_schema' || part.type === 'json_object' || part.type === 'output_json');
  if (jsonPart?.json) {
    raw = jsonPart.json;
  } else {
    raw = raw.map(part => part.text ?? '').join('\n');
  }
}

let data;
if (typeof raw === 'object') {
  data = raw;
} else {
  try {
    data = JSON.parse(raw);
  } catch (error) {
    throw new Error('Antwort konnte nicht als JSON geparst werden: ' + error.message);
  }
}

if (!data || !Array.isArray(data.prozesse)) {
  throw new Error('Die KI-Antwort enthÃ¤lt keine gÃ¼ltige Prozessliste.');
}

const globalData = $getWorkflowStaticData('global');
const assigned = Array.isArray(globalData.assignedProcesses) ? globalData.assignedProcesses : [];

// Generiere eindeutige IDs fÃ¼r die Prozesse
const baseIndex = assigned.length;
console.log('=== PROZESSANTWORT PARSEN ===');
console.log('Anzahl neue Prozesse:', data.prozesse.length);
console.log('BaseIndex:', baseIndex);

for (let i = 0; i < data.prozesse.length; i++) {
  const prozess = data.prozesse[i];
  const prozessId = `proc_${String(baseIndex + i + 1).padStart(3, '0')}`;

  console.log(`Generiere ID fÃ¼r Prozess "${prozess.name}": ${prozessId}`);

  assigned.push({
    ...prozess,
    id: prozessId,  // Eindeutige Prozess-ID
    abteilung_id: prozess.abteilung_id,
    abteilung_name: prozess.abteilung_name,
  });
}

globalData.assignedProcesses = assigned;
console.log('Gesamt Prozesse in globalData:', assigned.length);

// FÃ¼ge IDs auch zu den ausgegebenen Prozessen hinzu
data.prozesse = data.prozesse.map((prozess, index) => ({
  ...prozess,
  id: `proc_${String(baseIndex + index + 1).padStart(3, '0')}`,
}));

return [
  {
    json: {
      prozesse: data.prozesse,
    },
  },
];

const input = $input.first()?.json ?? {};
const selected = Array.isArray(input.prozesse) ? input.prozesse : [];
const globalData = $getWorkflowStaticData('global');
const available = Array.isArray(globalData.availableProcesses) ? globalData.availableProcesses : [];

const selectedIds = new Set(selected.map(prozess => String(prozess.pcf_id || '').trim()));
const remaining = available.filter(proc => !selectedIds.has(String(proc.pcf_id || '').trim()));

globalData.availableProcesses = remaining;
const currentRemaining = Number(globalData.remainingDepartments ?? remaining.length);
const updatedRemaining = Math.max(0, currentRemaining - 1);
globalData.remainingDepartments = updatedRemaining;

const isFinal = updatedRemaining === 0;

return [
  {
    json: {
      prozesse: selected,
      isFinal,
    },
  },
];

const input = $input.first()?.json ?? {};
if (!input.isFinal) {
  return [];
}

const globalData = $getWorkflowStaticData('global');
const context = globalData.context || {};

