  if (config.use_personality_based_descriptions) {
    const motivation = mitarbeiterKontext.motivation || 'mittel';
    const intelligenz = mitarbeiterKontext.intelligenz || 'mittel';

    // Qualitätslevel berechnen
    const qualityMap = { 'hoch': 3, 'mittel': 2, 'niedrig': 1 };
    const motivScore = qualityMap[motivation] || 2;
    const intScore = qualityMap[intelligenz] || 2;
    const avgScore = (motivScore + intScore) / 2;

    if (avgScore >= 2.5) {
      // Hoch: Detailliert, kritisch, lang
      qualityInstruction = `\n\nQUALITÄT: Du bist hochmotiviert und intelligent. Deine Beschreibung soll:
- Sehr detailliert sein (mindestens 150 Wörter)
- Kritische Gedanken und Herausforderungen benennen
- Konkrete Beispiele und Zahlen enthalten
- Optimierungspotenziale aufzeigen
- Professionell und präzise formuliert sein`;
      temperature = 0.4;
    } else if (avgScore >= 1.5) {
      // Mittel: Standard
      qualityInstruction = `\n\nQUALITÄT: Du bist durchschnittlich motiviert. Deine Beschreibung soll:
- Mittlere Detailtiefe haben (80-120 Wörter)
- Die wichtigsten Schritte beschreiben
- Grundlegende Informationen enthalten`;
      temperature = 0.6;
    } else {
      // Niedrig: Kurz, oberflächlich
      qualityInstruction = `\n\nQUALITÄT: Du bist wenig motiviert und beschreibst nur das Nötigste. Deine Beschreibung soll:
- Sehr kurz sein (30-60 Wörter)
- Nur die absoluten Basics erwähnen
- Einfache Sprache verwenden
- Keine Details oder Optimierungen nennen`;
      temperature = 0.7;
    }

    systemPrompt += qualityInstruction;
  } else {
    // Standard: Gute, genaue Beschreibung
    systemPrompt += `\n\nDeine Beschreibung soll detailliert, genau und professionell sein. Erkläre die Aktivität so, als würdest du einem neuen Kollegen erklären, wie du sie durchführst.`;
    temperature = 0.8;
  }

  // User Prompt
  const userPrompt = [
    'Beschreibe folgende Aktivität aus deiner Perspektive (Ich-Form):',
    '',
    `Aktivität: ${aktivitaet.name}`,
    `Prozess: ${prozessKontext.name}`,
    `Abteilung: ${prozessKontext.abteilung_name}`,
    '',
    aktivitaet.beschreibung ? `Kontext: ${aktivitaet.beschreibung}` : '',
    '',
    'Beschreibe wie DU diese Aktivität durchführst, welche Tools/Methoden du nutzt, und was die größten Herausforderungen sind.',
    '',
    'Gib das Ergebnis als JSON zurück: {"beschreibung_mitarbeiter": "..."}'
  ].filter(line => line !== '').join('\n');

  return {
    model: 'gpt-4o-mini',
    temperature: temperature,
    response_format: { type: 'json_object' },
    messages: [
      { role: 'system', content: systemPrompt },
      { role: 'user', content: userPrompt }
    ]
  };
})() }}

