const getNodeJson = (nodeName) => {
  try {
    const item = $(nodeName).first();
    return item?.json ?? {};
  } catch (error) {
    return {};
  }
};

const prozessData = getNodeJson('JSON parsen (Prozesse)');
const {
  unternehmen = {},
  abteilungen = [],
  rollen = [],
  prozesse = [],
  mitarbeiter: existingMitarbeiter = [],
  apqcProcesses = [],
  apqcActivities = [],
  apqcActivitiesByProcess = {},
  config = {},
} = prozessData;

// Wenn bereits Mitarbeiter vorhanden sind (Demo-Daten), diese verwenden
if (existingMitarbeiter && existingMitarbeiter.length > 0) {
  console.log('=== DEMO-MITARBEITER VERWENDEN ===');
  console.log('Anzahl:', existingMitarbeiter.length);
  return [{ json: { mitarbeiter: existingMitarbeiter, isFinal: true } }];
}

// Sonst API Response parsen
const message = $input.first().json?.choices?.[0]?.message;
if (!message) {
  throw new Error('Antwort der Mitarbeiter-API fehlt oder ist leer.');
}

let raw = message.content;
if (Array.isArray(raw)) {
  const jsonPart = raw.find(part => part.type === 'json_schema' || part.type === 'json_object' || part.type === 'output_json');
  if (jsonPart?.json) {
    raw = jsonPart.json;
  } else {
    raw = raw.map(part => part.text ?? '').join('\n');
  }
}

if (typeof raw === 'object') {
  raw = JSON.stringify(raw);
}

const data = JSON.parse(raw);
let mitarbeiter = data.mitarbeiter ?? data;

if (!Array.isArray(mitarbeiter)) {
  mitarbeiter = [mitarbeiter];
}

// Hole globalData
const globalData = $getWorkflowStaticData('global');
const alleMitarbeiter = globalData.alleMitarbeiter || [];
const context = globalData.mitarbeiterContext || {};

// Füge neue Mitarbeiter hinzu mit fortlaufenden IDs
const startIndex = alleMitarbeiter.length;
mitarbeiter = mitarbeiter.map((ma, index) => ({
  ...ma,
  id: `emp_${String(startIndex + index + 1).padStart(3, '0')}`,
  unternehmen_id: unternehmen.id ?? '',
  vollname: `${ma.vorname ?? ''} ${ma.nachname ?? ''}`.trim(),
  geschlecht: ma.geschlecht || 'divers',
  motivation: ma.motivation || 'mittel',
  intelligenz: ma.intelligenz || 'mittel',
}));

// Speichere in globalData
globalData.alleMitarbeiter = [...alleMitarbeiter, ...mitarbeiter];

console.log('=== MITARBEITER PARSEN ===');
console.log('Neue Mitarbeiter:', mitarbeiter.length);
console.log('Gesamt:', globalData.alleMitarbeiter.length);
console.log('Verbleibende Rollen:', (context.rollen?.length || 0) - Math.floor(globalData.alleMitarbeiter.length / 2));

// Prüfe, ob alle Rollen abgearbeitet wurden
const alleRollenBearbeitet = globalData.alleMitarbeiter.length >= (context.rollen?.length || 0);

return [{ json: { mitarbeiter, isFinal: alleRollenBearbeitet } }];
