const mitarbeiterContext = globalData.mitarbeiterContext || {};
const alleMitarbeiter = globalData.alleMitarbeiter || [];

const result = {
  unternehmen: mitarbeiterContext.unternehmen || {},
  abteilungen: mitarbeiterContext.abteilungen || [],
  rollen: mitarbeiterContext.rollen || [],
  prozesse: mitarbeiterContext.prozesse || [],
  mitarbeiter: alleMitarbeiter,
  apqcProcesses: mitarbeiterContext.apqcProcesses || [],
  apqcActivities: mitarbeiterContext.apqcActivities || [],
  apqcActivitiesByProcess: mitarbeiterContext.apqcActivitiesByProcess || {},
  config: mitarbeiterContext.config || {},
};

console.log('=== MITARBEITERITERATION ABSCHLUSS ===');
console.log('Mitarbeiter gesamt:', result.mitarbeiter.length);
console.log('Rollen gesamt:', result.rollen.length);

// WICHTIG: Speichere Mitarbeiter in globalData für spätere Verwendung in Aktivitätsiteration
globalData.mitarbeiterData = result.mitarbeiter;

// Cleanup Mitarbeiter-spezifisches globalData
globalData.alleMitarbeiter = [];
globalData.mitarbeiterContext = {};

return [
  {
    json: result,
  },
];

const input = $input.first()?.json ?? {};
const {
  unternehmen = {},
  abteilungen = [],
  rollen = [],
  prozesse = [],
  mitarbeiter = [],
  aktivitaeten = [],
  apqcProcesses = [],
  apqcActivities = [],
  apqcActivitiesByProcess = {},
  config = {},
} = input;

// Erstelle Lookup-Maps
const prozessMap = new Map(prozesse.map(p => [p.pcf_id, p]));
const abteilungMap = new Map(abteilungen.map(a => [a.id, a]));
const rollenMap = new Map(rollen.map(r => [r.id, r]));

// Gruppiere Mitarbeiter nach Abteilung
const mitarbeiterByAbteilung = new Map();
mitarbeiter.forEach(ma => {
  const abtId = ma.abteilung_id;
  if (!mitarbeiterByAbteilung.has(abtId)) {
    mitarbeiterByAbteilung.set(abtId, []);
  }
  mitarbeiterByAbteilung.get(abtId).push(ma);
});

console.log('=== MITARBEITER-ZUORDNUNG ===');
console.log('Aktivitäten zu verarbeiten:', aktivitaeten.length);
console.log('Mitarbeiter verfügbar:', mitarbeiter.length);

// Ordne jedem Aktivität einen passenden Mitarbeiter zu
const aktivitaetenMitMitarbeiter = aktivitaeten.map((aktivitaet, index) => {
  // Finde den Prozess der Aktivität
  const prozess = prozessMap.get(aktivitaet.prozess_id);
  if (!prozess) {
    console.log('WARNUNG: Prozess nicht gefunden für Aktivität:', aktivitaet.id);
    return { ...aktivitaet, mitarbeiter_id: null };
  }

  // Finde die Abteilung des Prozesses
  const abteilungId = prozess.abteilung_id;
  const verfuegbareMitarbeiter = mitarbeiterByAbteilung.get(abteilungId) || [];

  if (verfuegbareMitarbeiter.length === 0) {
    console.log('WARNUNG: Keine Mitarbeiter in Abteilung', abteilungId, 'für Aktivität:', aktivitaet.id);
    return { ...aktivitaet, mitarbeiter_id: null };
  }

  // Wähle einen passenden Mitarbeiter
  // Strategie: Rollenwahl basierend auf Aktivitätspriorität
  // - Hohe Priorität → Senior/Manager-Rollen
  // - Mittlere/Niedrige → Junior/Mid-Level-Rollen
  let ausgewaehlterMitarbeiter;

  if (aktivitaet.prioritaet === 'hoch') {
    // Bevorzuge höhere Rollen (Executive, Manager)
    ausgewaehlterMitarbeiter = verfuegbareMitarbeiter.find(ma => {
      const rolle = rollenMap.get(ma.rolle_id);
      return rolle && (rolle.ebene === 'Executive' || rolle.ebene === 'Manager');
    }) || verfuegbareMitarbeiter[0];
