    ausgewaehlterMitarbeiter = verfuegbareMitarbeiter[mitarbeiterIndex];
  }

  return {
    ...aktivitaet,
    mitarbeiter_id: ausgewaehlterMitarbeiter.id,
  };
});

console.log('Aktivitäten mit Mitarbeiter-Zuordnung:', aktivitaetenMitMitarbeiter.length);

