parameters.jsCode:
const message = $input.first().json.choices[0].message;
const { unternehmen, config } = $('JSON parsen (Unternehmen)').first().json;
let raw = message.content;
if (Array.isArray(raw)) {
  const jsonPart = raw.find(part => part.type === 'json_object' || part.type === 'json_schema' || part.type === 'output_json');
  if (jsonPart && jsonPart.json) {
    raw = jsonPart.json;
  } else {
    raw = raw.map(part => part.text ?? '').join('\n');
  }
}
if (typeof raw === 'object') {
  raw = JSON.stringify(raw);
}
const data = JSON.parse(raw);
const abteilungen = data.abteilungen ?? data;
return { unternehmen, abteilungen, config };

parameters.jsonBody:
={{ ({
  model: 'gpt-4o-mini',
  response_format: {
    type: 'json_schema',
    json_schema: {
      name: 'rollen_response',
      schema: {
        type: 'object',
        additionalProperties: false,
        properties: {
          rollen: {
            type: 'array',
            minItems: 1,
            items: {
              type: 'object',
              additionalProperties: false,
              properties: {
                id: { type: 'string' },
                abteilung_id: { type: 'string' },
                titel: { type: 'string' },
                beschreibung: { type: 'string' },
                ebene: { type: 'string', enum: ['Entry', 'Mid', 'Senior', 'Lead', 'Executive'] },
                anzahl_personen: { type: 'integer', minimum: 1 },
                gehalt_min: { type: 'number' },
                gehalt_max: { type: 'number' },
                anforderungen: {
                  type: 'array',
                  items: { type: 'string' },
                  minItems: 2
                }
              },
              required: ['id', 'abteilung_id', 'titel', 'beschreibung', 'ebene', 'anzahl_personen', 'gehalt_min', 'gehalt_max', 'anforderungen']
            }
          }
        },
        required: ['rollen']
      }
    }
  },
  messages: [
    {
      role: 'system',
      content: 'Du bist ein HR-Experte. Erstelle Rollen-Definitionen als JSON, das exakt dem gelieferten Schema entspricht. Verwende nur Daten aus dem Kontext.'
    },
    {
      role: 'user',
      content: [
        'Definiere Rollen für das folgende Unternehmen und seine Abteilungen.',
        '',
        'Kontext (Unternehmen):',
        JSON.stringify({
          id: $json.unternehmen.id,
          name: $json.unternehmen.name,
          branche: $json.unternehmen.branche,
          mitarbeiteranzahl: $json.unternehmen.mitarbeiteranzahl,
          gruendungsjahr: $json.unternehmen.gruendungsjahr,
          hauptstandort: $json.unternehmen.hauptsitz
        }, null, 2),
        '',
        'Abteilungen (Input):',
        JSON.stringify($json.abteilungen, null, 2),
        '',
        'Anforderungen:',
        '- Definiere 2-6 Rollen pro Abteilung mit unterschiedlichen Verantwortungsniveaus.',
        '- Ebene muss einer der Werte Entry/Mid/Senior/Lead/Executive sein.',
        '- anzahl_personen pro Abteilung soll in Summe zur mitarbeiteranzahl passen.',
        '- Setze realistische Gehaltsbereiche basierend auf Branche und Ebene.',
        '- anforderungen muss mindestens zwei Skills enthalten.',
        '',
        'Gib nur JSON zurück, keine Erklärungen.'
      ].join('\n')
    }
  ],
  temperature: 0.6
}) }}

